import React, { useState } from 'react';
import DataTable from 'react-data-table-component';
import Modal from './Modal'; 

const DataTableDisplay = ({ data, columns }) => {
  const [selectedRow, setSelectedRow] = useState(null);

  const tableColumns = columns.map((header) => ({
    name: header,
    selector: header,
    sortable: true,
  }));

  const handleRowClick = (row) => {
    setSelectedRow(row);
  };

  const handleCloseModal = () => {
    setSelectedRow(null);
  };

  return (
    <div>
      <h2>Processed Data</h2>
      <DataTable
        columns={tableColumns}
        data={data}
        pagination
        paginationPerPage={10}
        paginationRowsPerPageOptions={[10, 20, 50, 100]}
        noDataComponent="No data found"
        onRowClicked={handleRowClick} // Add row click event handler
      />

      {selectedRow && (
        <Modal>
          <div className="modal-content">
            <h3>Row Details</h3>
            <table>
              <tbody>
                {Object.entries(selectedRow).map(([header, value]) => (
                  <tr key={header}>
                    <td>{header}</td>
                    <td>{value}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <button onClick={handleCloseModal}>Close</button>
          </div>
        </Modal>
      )}
    </div>
  );
};

export default DataTableDisplay;
