import React, { useState } from 'react';
import Papa from 'papaparse';
import FileParser from './FileParser';
import DataTableDisplay from './DataTableDisplay';
import DownloadData from './DownloadData';
import LoadingIndicator from './LoadingIndicator';
import './LoadingIndicator.css'; // Import the CSS for the loading indicator

const App = () => {
  const [parsedData, setParsedData] = useState(null);
  const [selectedColumn, setSelectedColumn] = useState(null);
  const [isLoading, setIsLoading] = useState(false); // State to track loading status

  const handleFileUpload = (file) => {
    setIsLoading(true); // Start loading when file is being uploaded

    Papa.parse(file, {
      complete: handleParsingComplete,
      header: true,
      skipEmptyLines: true,
    });
  };

  const handleParsingComplete = (results) => {
    const { data, meta } = results;
    const columnHeaders = Object.keys(data[0]);
    setParsedData({ columnHeaders, data });
    setIsLoading(false); // Stop loading when parsing is complete
  };

  const handleColumnSelection = (event) => {
    setSelectedColumn(event.target.value);
  };

  const handleTokenizeData = () => {
    if (!selectedColumn) {
      alert('Please select a column first.');
      return;
    }

    setIsLoading(true); // Start loading when data is being tokenized

    // Simulate a delay to show the loading indicator (you can remove this setTimeout in the actual implementation)
    setTimeout(() => {
      const tokenizedData = tokenizeData(parsedData.data, selectedColumn);
      setParsedData({ ...parsedData, data: tokenizedData });
      setIsLoading(false); // Stop loading when tokenization is complete
    }, 1000);
  };

  const tokenizeData = (data, selectedColumn) => {
    // Implementation of the tokenizeData function
    // ... (The previous implementation goes here)
  };

  const handleDownloadData = (filename, format) => {
    if (!parsedData || !filename) {
      return;
    }

    setIsLoading(true); // Start loading when data is being prepared for download

    // Simulate a delay to show the loading indicator (you can remove this setTimeout in the actual implementation)
    setTimeout(() => {
      let dataToDownload;
      if (format === 'csv') {
        const csvData = Papa.unparse(parsedData.data);
        dataToDownload = `data:text/csv;charset=utf-8,${encodeURIComponent(csvData)}`;
      } else if (format === 'json') {
        const jsonData = JSON.stringify(parsedData.data, null, 2);
        dataToDownload = `data:text/json;charset=utf-8,${encodeURIComponent(jsonData)}`;
      } else {
        setIsLoading(false); // Invalid format, stop loading and do not proceed with the download
        return;
      }

      const link = document.createElement('a');
      link.setAttribute('href', dataToDownload);
      link.setAttribute('download', `${filename}.${format}`);
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      setIsLoading(false); // Stop loading when download is complete
    }, 1000);
  };

  return (
    <div>
      {isLoading && <LoadingIndicator />} {/* Display the loading indicator if isLoading is true */}
      <FileParser
        parsedData={parsedData}
        selectedColumn={selectedColumn}
        handleFileUpload={handleFileUpload}
        handleColumnSelection={handleColumnSelection}
        handleTokenizeData={handleTokenizeData}
      />
      {parsedData && (
        <>
          <DataTableDisplay data={parsedData.data} columns={parsedData.columnHeaders} />
          <DownloadData onDownload={handleDownloadData} />
        </>
      )}
    </div>
  );
};

export default App;
