import React, { useState, useCallback } from 'react';
import DataTable from 'react-data-table-component';
import Modal from './Modal';
import './DataTableDisplay.css'; // Import the custom CSS file

const DataTableDisplay = ({ data, columns }) => {
  const [selectedRow, setSelectedRow] = useState(null);
  const [filters, setFilters] = useState({});

  const handleRowClick = useCallback((row) => {
    setSelectedRow(row);
  }, []);

  const handleCloseModal = useCallback(() => {
    setSelectedRow(null);
  }, []);

  const wrapText = (text, maxWidth) => {
    if (!text) return '';

    if (text.length <= maxWidth) return text;

    return text.slice(0, maxWidth) + '...';
  };

  const tableColumns = columns.map((header) => ({
    name: header,
    selector: header,
    sortable: true,
    cell: (row) => {
      const wrappedText = wrapText(row[header], 250);
      return (
        <div
          className="max-h-16 overflow-y-auto whitespace-wrap cursor-pointer"
          style={{ maxWidth: '250px' }}
          onClick={() => handleRowClick(row)}
        >
          {wrappedText}
        </div>
      );
    },
    subHeader: true, // Enable subheader for this column
    subHeaderComponent: (props) => {
      const filterKey = props.column.selector;
      return (
        <input
          type="text"
          className="filter-input"
          placeholder={`Filter ${props.column.name}`}
          value={filters[filterKey] || ''}
          onChange={(e) => setFilter(filterKey, e.target.value)}
        />
      );
    },
  }));

  const ExpandableRow = () => {
    return (
      <div>
        {selectedRow &&
          Object.entries(selectedRow).map(([header, value]) => (
            <p key={header}>
              <strong>{header}:</strong> {value}
            </p>
          ))}
      </div>
    );
  };

  const customFilter = (rows, filterField, filterValue) => {
    return rows.filter((row) => {
      const cellValue = row[filterField]?.toString()?.toLowerCase();
      return cellValue.includes(filterValue?.toLowerCase());
    });
  };

  const setFilter = (field, value) => {
    setFilters({ ...filters, [field]: value });
  };

  return (
    <div className="px-4 py-6">
      <h2 className="text-2xl font-bold mb-4">Processed Data</h2>
      <DataTable
        columns={tableColumns}
        data={data}
        pagination
        paginationPerPage={10}
        paginationRowsPerPageOptions={[10, 20, 50, 100]}
        noDataComponent={<span className="text-red-500">No data found</span>}
        expandableRows
        expandableRowsComponent={<ExpandableRow />}
        expandOnRowClicked
        highlightOnHover
        defaultSortField="id" // Replace "id" with the column name to be sorted by default
        defaultSortAsc // Set this to false if you want the default sort to be descending
        onSort={(column, sortDirection) => {
          // Implement sorting logic here if needed
          // You can handle the sorting based on the column name and sortDirection
        }}
        customFilterFunction={customFilter}
        subHeaderAlign="center" // Align the subheader input fields to the center
        subHeaderWrap // Wrap the subheader input fields in a div to prevent horizontal scroll
      />
      {selectedRow && (
        <Modal onClose={handleCloseModal}>
          <div className="modal-content">
            <h3 className="text-lg font-semibold mb-2">Row Details</h3>
            <table>
              <tbody>
                {Object.entries(selectedRow).map(([header, value]) => (
                  <tr key={header}>
                    <td className="font-semibold pr-2">{header}</td>
                    <td>{value}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </Modal>
      )}
    </div>
  );
};

export default DataTableDisplay;
