import React, { useState } from 'react';

const DownloadData = ({ data, columns }) => {
  const [filename, setFilename] = useState('');

  const handleDownloadCSV = () => {
    if (!data || !columns) return;

    const csvContent = generateCSVContent(data, columns);
    downloadFile(csvContent, 'csv', filename);
  };

  const handleDownloadJSON = () => {
    if (!data) return;

    const jsonData = JSON.stringify(data, null, 2);
    downloadFile(jsonData, 'json', filename);
  };

  const generateCSVContent = (data, columns) => {
    const headerRow = columns.join(',') + '\n';
    const dataRows = data.map((row) => {
      return columns.map((header) => row[header]).join(',');
    });

    return headerRow + dataRows.join('\n');
  };

  const downloadFile = (content, fileType, filename) => {
    const blob = new Blob([content], { type: `text/${fileType}` });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `${filename}.${fileType}`;
    link.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div>
      <h2>Download Data</h2>
      <input
        type="text"
        value={filename}
        placeholder="Enter a filename"
        onChange={(e) => setFilename(e.target.value)}
      />
      <button onClick={handleDownloadCSV}>Download as CSV</button>
      <button onClick={handleDownloadJSON}>Download as JSON</button>
    </div>
  );
};

export default DownloadData;
